 {params : {kode : 'Scary Movie'}},
 {params : {kode : 'Bloodshot'}},
 {params : {kode : 'Maleficent'}},
 {params : {kode : 'Rumble'}},
 {params : {kode : 'DWP X'}},
 {params : {kode : 'Indonesia Nostalgia Festival'}},
 {params : {kode : 'Music Festival 1'}},
 {params : {kode : 'Music Festival 2'}},
 {params : {kode : 'Konser Raisa'}},
 {params : {kode : 'Jessie J'}},
 {params : {kode : 'Blues Festival'}},
 {params : {kode : 'Showcase Concert Afgan'}},
 {params : {kode : 'Seafood Festival'}},
 {params : {kode : 'Food Truck Festival'}},
 {params : {kode : 'Fall Festival'}},
 {params : {kode : 'Acoustic Festival'}},

 ],
 fallback : false
 }
}
export default Artikel;
